import requests
from bs4 import BeautifulSoup
import pandas as pd

url = "https://twitter.com/search?q=%20(inteligencia%20artificial)%20until%3A2022-12-31%20since%3A2018-01-01%20geocode%3A-25.274398%2C-57.634666%2C100km&src=typed_query&f=live"
response = requests.get(url)
soup = BeautifulSoup(response.content, 'html.parser')

tweets = []
for tweet in soup.find_all('div', {'data-testid': 'tweetText'}):
    tweet_text = tweet.find('div', {"class": "css-901oao r-1nao33i r-37j5jr r-a023e6 r-16dba41 r-rjixqe r-bcqeeo r-bnwqim r-qvutc0"}).text
    tweets.append(tweet_text)

df = pd.DataFrame({'Tweets':tweets})
print(df)


